class BBreadcrumbs extends HTMLElement{constructor(){super();this.breadcrumbs=[];this.cookieName='breadcrumbs';this.maxBreadcrumbs=20;this.displayLimit=10;}
connectedCallback(){this.loadFromCookie();this.render();}
add(path,title,scrollY=0){const normalizedPath=this.normalizePath(path);this.breadcrumbs=this.breadcrumbs.filter(b=>this.normalizePath(b.path)!==normalizedPath);this.breadcrumbs.unshift({path:normalizedPath,title,scrollY});if(this.breadcrumbs.length>this.maxBreadcrumbs){this.breadcrumbs=this.breadcrumbs.slice(0,this.maxBreadcrumbs);}
this.saveToCookie();this.render();}
getScrollPosition(path){const normalizedPath=this.normalizePath(path);const breadcrumb=this.breadcrumbs.find(b=>this.normalizePath(b.path)===normalizedPath);return breadcrumb?breadcrumb.scrollY:0;}
async navigateToPath(path){const normalizedPath=this.normalizePath(path);const hash=window.location.hash;const currentPath=hash.startsWith('#!')?hash.substring(2):'Resume';const currentScrollY=window.Router?window.Router.getScrollPosition():0;const currentBreadcrumb=this.breadcrumbs.find(b=>this.normalizePath(b.path)===this.normalizePath(currentPath));if(currentBreadcrumb){currentBreadcrumb.scrollY=currentScrollY;this.saveToCookie();}
let breadcrumb=this.breadcrumbs.find(b=>this.normalizePath(b.path)===normalizedPath);if(!breadcrumb){const title=this.generateTitle(normalizedPath);this.add(normalizedPath,title,0);breadcrumb=this.breadcrumbs[0];}else{this.breadcrumbs.splice(this.breadcrumbs.indexOf(breadcrumb),1);this.breadcrumbs.unshift(breadcrumb);this.saveToCookie();this.render();}
const scrollY=breadcrumb.scrollY;const shouldScroll=scrollY===0;await window.Router.loadContentFromPath(breadcrumb.path,shouldScroll);if(scrollY>0){await new Promise(resolve=>{requestAnimationFrame(()=>{requestAnimationFrame(()=>{resolve();});});});window.Router.setScrollPosition(scrollY);const updatedBreadcrumb=this.breadcrumbs.find(b=>this.normalizePath(b.path)===normalizedPath);if(updatedBreadcrumb){updatedBreadcrumb.scrollY=scrollY;this.saveToCookie();}}}
normalizePath(path){if(path.startsWith('#!')){path=path.substring(2);}else if(path.startsWith('/')){path=path.substring(1);}else if(path.startsWith('#')){path=path.substring(1);}
return path.replace(/^\/+|\/+$/g,'').replace(/\/+/g,'/')||'Resume';}
generateTitle(path){let title=path.replace(/-slash-/g,'/');if(title.toLowerCase()==='history'){return'Timeline';}
if(title.includes('/')){const parts=title.split('/');const formattedParts=parts.map((part,index)=>{if(part.length===0)return part;const words=part.replace(/-/g,' ').split(' ');const capitalized=words.map(word=>word.length>0?word.charAt(0).toUpperCase()+word.slice(1):word).join(' ');return capitalized;});if(formattedParts.length===2){title=`${formattedParts[0]}: ${formattedParts[1]}`;}else{title=formattedParts.join(' / ');}}else{title=title.replace(/-/g,' ');if(title.length>0){title=title.charAt(0).toUpperCase()+title.slice(1);}}
return title;}
render(){const ul=this.querySelector('ul');if(!ul)return;ul.innerHTML='';const breadcrumbsToDisplay=this.breadcrumbs.slice(0,this.displayLimit);breadcrumbsToDisplay.forEach((breadcrumb,index)=>{const li=document.createElement('li');const a=document.createElement('a');a.href=`#!${breadcrumb.path}`;let icon='';const path=breadcrumb.path.toLowerCase();if(path.startsWith('projects/')){icon='<i class="fa-solid fa-rocket" aria-hidden="true"></i> ';}else if(path.startsWith('skillsets/')){icon='<i class="fa-solid fa-layer-group" aria-hidden="true"></i> ';}else if(path.startsWith('skills/')){icon='<i class="fa-solid fa-wrench" aria-hidden="true"></i> ';}else if(path.startsWith('timeline-events/')){icon='<i class="fa-solid fa-clock" aria-hidden="true"></i> ';}else if(path.startsWith('subdomains/')){icon='<i class="fa-solid fa-sitemap" aria-hidden="true"></i> ';}else if(path==='resume'){icon='<i class="fa-solid fa-file-lines" aria-hidden="true"></i> ';}else if(path==='projects'){icon='<i class="fa-solid fa-folder-open" aria-hidden="true"></i> ';}else if(path==='history'){icon='<i class="fa-solid fa-clock-rotate-left" aria-hidden="true"></i> ';}else if(path==='skillsets'){icon='<i class="fa-solid fa-tags" aria-hidden="true"></i> ';}else if(path==='about-me'){icon='<i class="fa-solid fa-user" aria-hidden="true"></i> ';}else if(path==='my-stance-on-ai'){icon='<i class="fa-solid fa-robot" aria-hidden="true"></i> ';}else if(path==='contact'){icon='<i class="fa-solid fa-envelope" aria-hidden="true"></i> ';}
a.innerHTML=icon+breadcrumb.title;a.setAttribute('tabindex','-1');a.setAttribute('aria-hidden','true');if(index===0){a.classList.add('active');}
a.addEventListener('click',async(e)=>{e.preventDefault();await this.navigateToPath(breadcrumb.path);});li.appendChild(a);ul.appendChild(li);});}
saveToCookie(){try{const data=JSON.stringify(this.breadcrumbs);const expires=new Date();expires.setTime(expires.getTime()+(365*24*60*60*1000));document.cookie=`${this.cookieName}=${encodeURIComponent(data)};expires=${expires.toUTCString()};path=/`;}catch(e){console.warn('Failed to save breadcrumbs to cookie:',e);}}
loadFromCookie(){try{const cookies=document.cookie.split(';');const cookie=cookies.find(c=>c.trim().startsWith(`${this.cookieName}=`));if(cookie){const data=decodeURIComponent(cookie.split('=')[1]);this.breadcrumbs=JSON.parse(data)||[];this.breadcrumbs=this.breadcrumbs.filter(b=>b&&typeof b.path==='string'&&typeof b.title==='string');}}catch(e){console.warn('Failed to load breadcrumbs from cookie:',e);this.breadcrumbs=[];}}}
customElements.define('b-breadcrumbs',BBreadcrumbs);