class BDate extends HTMLElement{connectedCallback(){const value=this.getAttribute('value');if(value){this.textContent=BDate.formatDate(value);}else{this.textContent=new Date().getFullYear();}}
static formatDate(dateStr){if(!dateStr||dateStr==='Unknown'||dateStr==='null'||dateStr===null){return'Unknown';}
const parts=dateStr.split('-');const year=parts[0];if(parts.length===3){const month=parseInt(parts[1])-1;const day=parseInt(parts[2]);const monthNames=['January','February','March','April','May','June','July','August','September','October','November','December'];const daySuffix=BDate.getDaySuffix(day);return`${monthNames[month]} ${day}${daySuffix}, ${year}`;}else if(parts.length===2){const part2=parts[1].toLowerCase();if(part2==='early'){return`Early ${year}`;}else if(part2==='mid'){return`Mid ${year}`;}else if(part2==='late'){return`Late ${year}`;}else if(part2==='spring'){return`Spring ${year}`;}else if(part2==='summer'){return`Summer ${year}`;}else if(part2==='fall'){return`Fall ${year}`;}else if(part2==='winter'){return`Winter ${year}`;}else{const secondPart=parts[1];if(secondPart.length===4&&/^\d{4}$/.test(secondPart)){return`${year}-${secondPart}`;}else{const month=parseInt(parts[1])-1;if(isNaN(month)||month<0||month>11){return`${year}-${secondPart}`;}
const monthNames=['January','February','March','April','May','June','July','August','September','October','November','December'];return`${monthNames[month]}, ${year}`;}}}else{return year;}}
static getDaySuffix(day){if(day>=11&&day<=13){return'th';}
switch(day%10){case 1:return'st';case 2:return'nd';case 3:return'rd';default:return'th';}}}
customElements.define('b-date',BDate);