class BSkipLink extends HTMLElement{connectedCallback(){const target=this.getAttribute('target')||'main-content';const label=this.getAttribute('label')||'Skip to main content';const link=document.createElement('a');link.href=`#${target}`;link.className='skip-link';link.textContent=label;link.addEventListener('click',(e)=>{e.preventDefault();const targetElement=document.getElementById(target);if(targetElement){if(!targetElement.hasAttribute('tabindex')){targetElement.setAttribute('tabindex','-1');}
targetElement.focus();targetElement.scrollIntoView({behavior:'smooth',block:'start'});}});this.innerHTML='';this.appendChild(link);}}
customElements.define('b-skip-link',BSkipLink);